var Promise = require('bluebird')
var fs = Promise.promisifyAll(require('fs-extra'))

var ApiError = require('../util/api-error')
var _install = require('./_install')

var App = module.exports = {}
const APPS_DIR = process.env.APPS_DIR

App.modules = function (done) {
  fs.readdirAsync(APPS_DIR).then(function (files) {
    return Promise.filter(files, function (file, callback) {
      return fs.lstatAsync(APPS_DIR + '/' + file).then(function (stats) {
        return stats.isDirectory()
      })
    }).then(function (directories) {
      return Promise.map(directories, function (directory) {
        return new Promise(function (resolve, reject) {
          App.getPackageJson(directory, function (err, pkg) {
            if (err) return reject(err)
            return resolve(pkg)
          })
        })
      })
    }).then(function (packages) {
      packages = packages.filter(function (pkg) { return pkg }) // Avoid crash when app folder is empty
      return done(null, packages)
    }).catch(done)
  })
}

App.all = function (done) {
  App.modules(function (err, apps) {
    if (err) return done(err)

    const plugins = apps.filter(function (app) {
      return !(app.netbeast && app.netbeast.type === 'plugin')
    })

    done(null, plugins)
  })
}

App.plugins = function (done) {
  App.modules(function (err, apps) {
    if (err) return done(err)
    const plugins = apps.filter(function (app) {
      return app.netbeast && app.netbeast.type === 'plugin'
    })

    done(null, plugins)
  })
}

App.delete = function (app, done) {
  if (!fs.existsSync(path.join(APPS_DIR, app))) {
    return done(new ApiError(404, app + ' is not installed'))
  }

  fs.remove(path.join(APPS_DIR, app), done)
}

App.getPackageJson = function (app, done) {
  const dir = path.join(APPS_DIR, app)
  if (!fs.existsSync(dir)) {
    return done(new ApiError(404, app + ' is not installed'))
  }

  fs.readJson(path.join(dir, 'package.json'), function (err, pkg) {
    if (err && err.code === 'ENOENT') return done()
    done(err, pkg)
  })
}

App.install = function (bundle, done) {
  if (_isUrl(bundle)) return _install.from.git(bundle, done)

  fs.lstat(bundle, function (err, stats) {
    if (err) return done(err)

    if (stats.isDirectory()) return _install.from.dir(bundle, done)

    return done(new ApiError(422, 'App does not have proper format'))
  })
}

App.find = function (query, done) {
  // only querying topics by the moment
  App.all(function (err, apps) {
    if (err) return done(err)

    var result = apps.filter(function (app) {
      return app.netbeast && app.netbeast.topic === query.topic
    })

    done(null, result)
  })
}

function _isUrl (s) {
  var regexp = /(git|ftp|http|https):\/\/(\w+:{0,1}\w*@)?(\S+)(:[0-9]+)?(\/|\/([\w#!:.?+=&%@!\-\/]))?/
  return regexp.test(s)
}

var path = require('path')
var exec = require('child_process').exec

var request = require('superagent')
var git = require('gift')
var fs = require('fs-extra')

var ApiError = require('../util/api-error')
var broker = require('../helpers/broker')

function _installDeps (app, done) {
  const root = path.join(process.env.APPS_DIR, app.name)
  const modules = path.join(root, 'node_modules')

  if (fs.existsSync(modules)) {
    return done(null, app)
  }

  broker.info('Downloading ' + app.name + ' dependencies...')

  exec('npm i', {cwd: root}, function (err, data) {
    return done(err, app) // data is not app
  })
}

function _installFromDir (dir, done) {
  const file = path.join(dir, 'package.json')

  if (!fs.existsSync(file)) {
    return done(new Error('App does not have a package.json'))
  }

  const appJson = fs.readJsonSync(file, {throws: false})
  if (!appJson) {
    return done(new Error("App's package.json is malformed"))
  }

  const appRoot = path.join(process.env.APPS_DIR, appJson.name)
  if (fs.existsSync(appRoot)) {
    return done(new ApiError(422, 'App already exists'))
  }

  // Check if main is an executable file
  const main = path.resolve(dir, appJson.main)
  if (!fs.existsSync(main)) {
    return done(new Error('App does not have a main executable'))
  } else {
    fs.chmodSync(main, '700')
  }

  broker.info('Setting everything up for you...')

  fs.move(dir, appRoot, function (err) {
    if (err) return done(err)

    _installDeps(appJson, function (err, appJson) {
      if (!err) return done(null, appJson)

      // Swapping if (err) we get rid of a nesting level
      fs.remove(appRoot, function (removalError) {
        if (removalError) broker.error(removalError.message)
        done(err)
      })
    })
  })
}

function _installFromGit (url, done) {
  var tmpDir = path.join(process.env.TMP_DIR, '' + new Date().getTime())
  git.clone(url, tmpDir, function (err, repo) {
    if (err) return done(err)

    _installFromDir(repo.path, done)
  })
}

module.exports = {
  from: {
    dir: _installFromDir,
    git: _installFromGit
  }
}

// Broker.js is an instance for socket.io
// that logs messages to refactor code

var chalk = require('chalk')

var mqtt = require('mqtt')
var client = mqtt.connect('ws://localhost:' + process.env.PORT)

var broker = module.exports = {}

broker.client = client

broker.client.on('connect', function () {
  console.log('[broker] online')
  broker.client.subscribe('#')
})

client.on('message', function (topic, message) {
  topic = topic.toString()

  try {
    message = JSON.parse(message.toString())
    if (topic === 'netbeast/push') notificationLogger(message)
  } catch (e) {
    console.log('broker got a non JSON object')
    message = message.toString()
  }
  broker.client.emit('#' + topic, message)
})

broker.on = function (topic, cb) {
  broker.client.on(topic, cb)
}

// title is optional
broker.info = function (body, title) {
  broker.emit({ emphasis: 'info', body: body, title: title })
}

broker.error = function (body, title) {
  broker.emit({ emphasis: 'error', body: body, title: title })
}

broker.success = function (body, title) {
  broker.emit({ emphasis: 'success', body: body, title: title })
}

broker.warning = function (body, title) {
  broker.emit({ emphasis: 'warning', body: body, title: title })
}

broker.emit = function (msg) {
  client.publish('netbeast/push', JSON.stringify(msg))
}

function notificationLogger (msg) {
  // Log notification through console
  var str = ''

  msg.emphasis = msg.emphasis ||Â 'info'
  msg.body = ' ' + msg.body + ' '

  switch (msg.emphasis) {
    case 'error':
      str = chalk.bold.bgRed(' #' + msg.title + ' ')
      str = str + chalk.bgRed(msg.body)
      break
    case 'warning':
      str = chalk.bold.bgYellow(' #' + msg.title + ' ')
      str = str + chalk.bgYellow(msg.body)
      break
    case 'info':
      str = chalk.bold.bgCyan(' #' + msg.title + ' ')
      str = str + chalk.bgCyan(msg.body)
      break
    case 'success':
      str = chalk.bold.bgGreen(' #' + msg.title + ' ')
      str = str + chalk.bgGreen(msg.body)
      break
  }

  console.log(str)
}

