function Resource (item) {

  this.id = item.id
  this.alias = item.alias
  this.app = item.app
  this.location = item.location
  this.topic = item.topic
  this.groupname = item.groupname
  this.hook = item.hook

  try {
    const hookTail = item.hook.split('/')[item.hook.split('/').length - 1]
    if (macRegex.test(hookTail)) this.mac = hookTail
    if (ipRegex.test(hookTail)) this.ip = hookTail
  } catch (e) { /* console.log(chalk.grey('[warning] rosources without hook')) */ }
}

Resource.create = function (item, done) {
  return (new Resource(item)).save(done)
}

Resource.prototype.destroy = function (done) {
  helper.deleteAction(this.id, function (err) {
    if (err) return done(err)

      if (typeof done === 'function') return done(null)
    })
}

Resource.find = function (query, done) {
  var result = []
  helper.findAction(query, function (err, row) {
    if (err) return done(err)
      if (_.isEmpty(row)) return done(new ApiError(404, 'Resource not found DB'))

      row.forEach(function (action) {
        result.push(new Resource(action))
      })

      return done(null, result)
    })
}

Resource.findOne = function (query, done) {
  helper.findAction(query, function (err, row) {
    if (err) return done(err)
      if (row.length < 1) return done(new ApiError(404, 'Resource not found DB'))

        return done(null, new Resource(row[row.length - 1]))
    })
}

Resource.prototype.save = function (done) {
  var self = this
  var schema = {
    alias: this.alias,
    app: this.app,
    location: this.location,
    topic: this.topic,
    groupname: this.groupname,
    hook: this.hook
  }
  helper.insertAction(schema,	function (err) {
    if (err) return done(err)

      Resource.findOne({app: self.app}, done)
  })
}

Resource.update = function (query, value, done) {
  helper.updateAction(query, value, function (err) {
    if (err) return done(err)

      return done()
  })
}

Resource.destroy = function (query, done) {
  Resource.find(query, function (err, resources) {
    if (err && err.statusCode !== 404) {
      return done(err)
    }

    if (!resources || _.isEmpty(resources)) {
      if (typeof done === 'function') return done(null, 0)
    } else {
      Promise.map(resources, function (item) {
        return new Promise(function (resolve, reject) {
          item.destroy(function (err) {
            if (err) return reject(err)
            return resolve()
          })
        })
      }).then(function () {
        if (typeof done === 'function') return done()
      }).catch(done)
    }
  })
}

module.exports = Resource

var sqlite3 = require('sqlite3').verbose()
var crypto = require('crypto')

var db = new sqlite3.Database(process.env.DATABASE_URI)

var helper = module.exports = {}

helper.createTable = function (done) {
  db.run('CREATE TABLE IF NOT EXISTS resources(' +
  'id TEXT PRIMARY KEY NOT NULL, ' +
  'alias TEXT, ' +
  'app TEXT NOT NULL, ' +
  'location TEXT, ' +
  'topic TEXT NOT NULL, ' +
  'groupname TEXT, ' +
  'hook TEXT NOT NULL)',
  done)
}

helper.insertAction = function (query, done) {
  var id = crypto.createHash('sha1').update(query.app + query.hook).digest('hex')
  var statement = db.prepare('INSERT INTO resources (' +
  "'id', 'alias', 'app', 'location', 'topic', 'groupname', 'hook') " +
  'VALUES (?,?,?,?,?,?,?)')
  statement
  .run(id, query.alias, query.app, query.location, query.topic, query.groupname, query.hook, done)
  statement.finalize()
}

helper.deleteAction = function (id, done) {
  db.run("DELETE FROM resources WHERE id = '" + id + "'", done)
}

helper.findAction = function (query, done) {
  if (Object.keys(query).length < 1) {
    // Devuelve los diferentes Hooks que identifican al objeto
    db.all('SELECT * FROM resources', done)
  } else {
    var sql_statement = 'SELECT * FROM resources WHERE '
    for (var field in query) {
      sql_statement += field + "='" + query[field] + "' AND "
    }
    var aux = sql_statement.lastIndexOf('AND')
    sql_statement = sql_statement.substring(0, aux)
    db.all(sql_statement, done)
  }
}

helper.updateAction = function (query, value, done) {
  var key = Object.keys(query)[0]
  var newvalue = Object.keys(value)[0]
  db.run('UPDATE resources SET ' + newvalue + "='" + value[newvalue] +
  "' WHERE " + key + "='" + query[key] + "'", done)
}
